import { useContextProvider } from '../context/ContextProvider'

function Remaining() {

  const [{budget,expenses},dispatch] = useContextProvider();

  const[remaining , setRemaining] = useState(budget)
  
  useEffect ( () => {
  const totalCost = 0
    expenses.forEach(element => {
      let expensesRemaing = totalCost += element.cost
      setRemaining(budget-expensesRemaing)
    });
  } , [expenses])

  return (
    <div className='alert alert-success p-4 fw-bold'>
        <span>
            Remaining : ${remaining}   
        </span>
    </div>
  )
}

